    string constant ERROR_QUORUM_IS_NOT_REACHED = "ERROR_QUORUM_IS_NOT_REACHED";
    string constant ERROR_TREASURY_INSUFFICIENT_BALANCE = "ERROR_TREASURY_INSUFFICIENT_BALANCE";
