pragma solidity ^0.8.0;

import "contracts/Uint256Helpers.sol";
import "contracts/Constants.sol";

struct Payment {
    address token;
    address destination;
    uint256 amount;
    bool sendETH;
}

struct Proposal {
    uint256 hash;
    uint32 createdTimestamp; // until 2105 year
    uint32 createdBlockNumber;
    Payment payment;
    // init zeros:
    uint32 updatedBlockNumber;
    uint32 yeas;
    uint32 nays;
    bool isExecuted;
    bool vetoed;
}

library ProposalLibrary {
    using Uint256Helpers for uint256;

    // time-to-live(TTL) of proposal is 3 days
    // after that time proposal becomes “discarded” if not enough votes are gathered
    uint32 constant PROPOSAL_TTL = 3 days;

    function emptyPayment() internal pure returns (Payment memory) {
        return Payment({token: address(0), destination: address(0), amount: 0, sendETH: false});
    }

    function isExpired(Proposal storage proposal) internal view returns (bool) {
        return proposal.createdTimestamp + PROPOSAL_TTL < block.timestamp;
    }

    function isQuorumReached(Proposal storage proposal) internal view returns (bool) {
        return isAccepted(proposal) || isRejected(proposal);
    }

    function isRejected(Proposal storage proposal) internal view returns (bool) {
        return proposal.nays >= Constants.MIN_QUORUM;
    }

    function isAccepted(Proposal storage proposal) internal view returns (bool) {
        return proposal.yeas >= Constants.MIN_QUORUM;
    }

    function isActive(Proposal storage proposal) internal view returns (bool) {
        return proposal.createdBlockNumber > 0 && !isRejected(proposal) && !proposal.isExecuted;
    }

    function vote(
        Proposal storage proposal,
        bool support,
        uint32 votingPower
    ) internal {
        if (support) {
            proposal.yeas += votingPower;
        } else {
            proposal.nays += votingPower;
        }
        proposal.updatedBlockNumber = block.number.toUint32();
    }

    function isSupported(Proposal storage proposal) internal view returns (bool) {
        return proposal.yeas > proposal.nays;
    }
}
","keccak256":"0x22add5d9adff48e50568af0e4f5a2b299f7359951ea5d82807eebded64195569","license":"MIT"},"contracts/ProposalQueue.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "contracts/Uint256Helpers.sol";
import "contracts/Errors.sol";
import "contracts/Proposal.sol";

contract ProposalQueue {
    using Uint256Helpers for uint256;
    using ProposalLibrary for Proposal;

    // There are no more than N=3 current proposals, new proposals cannot be added until
    // old ones will be “accepted”, “declined” or “discarded” by TTL
    uint8 public constant MAX_ACTIVE_PROPOSALS = 10;

    Proposal[MAX_ACTIVE_PROPOSALS] public proposals;

    function countYeas(uint256 hash) external view returns (uint32) {
        (bool found, uint8 index) = find(hash);

        if (!found) return 0;

        return proposals[index].yeas;
    }

    /**
    Find index of a proposal by hash that could be in any state: active, expired or finished
     */
    function find(uint256 hash) public view returns (bool, uint8) {
        for (uint8 i; i < MAX_ACTIVE_PROPOSALS; i++) {
            if (proposals[i].hash == hash) {
                return (true, i);
            }
        }
        return (false, 0);
    }

    function findInactive() internal view returns (bool, uint8) {
        for (uint8 i = 0; i < MAX_ACTIVE_PROPOSALS; i++) {
            if (!proposals[i].isActive()) {
                return (true, i);
            }
        }
        return (false, 0);
    }

    /**
    Append a new proposal to the queue or replace an existing inactive proposal by the new one
     */
    function enqueue(Proposal memory proposal) internal {
        (bool found, uint8 index) = findInactive();
        require(found, Errors.ERROR_QUEUE_IS_FULL);

        proposals[index] = proposal;
    }
}
